import axios from 'axios';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
export default {
  data: function data() {
    return {
      dialog: false,
      search: '',
      ventas: [],
      headers: [{
        text: 'Opciones',
        value: 'opciones',
        sortable: false
      }, {
        text: 'Usuario',
        value: 'usuario.nombre',
        sortable: true
      }, {
        text: 'Cliente',
        value: 'persona.nombre',
        sortable: true
      }, {
        text: 'Tipo Comprobante',
        value: 'tipo_comprobante',
        sortable: true
      }, {
        text: 'Serie comprobante',
        value: 'serie_comprobante',
        sortable: false
      }, {
        text: 'Número comprobante',
        value: 'num_comprobante',
        sortable: false
      }, {
        text: 'Fecha',
        value: 'createdAt',
        sortable: false
      }, {
        text: 'Impuesto',
        value: 'impuesto',
        sortable: false
      }, {
        text: 'Total',
        value: 'total',
        sortable: false
      }, {
        text: 'Estado',
        value: 'estado',
        sortable: false
      }],
      _id: '',
      persona: '',
      personas: [],
      tipo_comprobante: '',
      comprobantes: ['BOLETA', 'FACTURA', 'TICKET', 'GUIA'],
      serie_comprobante: '',
      num_comprobante: '',
      impuesto: 0.18,
      codigo: '',
      cabeceraDetalles: [{
        text: 'Borrar',
        value: 'borrar',
        sortable: false
      }, {
        text: 'Artículo',
        value: 'articulo',
        sortable: false
      }, {
        text: 'Cantidad',
        value: 'cantidad',
        sortable: false
      }, {
        text: 'Precio',
        value: 'precio',
        sortable: false
      }, {
        text: 'Descuento',
        value: 'descuento',
        sortable: false
      }, {
        text: 'Sub Total',
        value: 'subtotal',
        sortable: false
      }],
      detalles: [],
      verNuevo: 0,
      errorArticulo: null,
      total: 0,
      totalParcial: 0,
      totalImpuesto: 0,
      articulos: [],
      texto: '',
      cabeceraArticulos: [{
        text: 'Seleccionar',
        value: 'seleccionar',
        sortable: false
      }, {
        text: 'Código',
        value: 'codigo',
        sortable: false
      }, {
        text: 'Nombre',
        value: 'nombre',
        sortable: true
      }, {
        text: 'Categoría',
        value: 'categoria.nombre',
        sortable: true
      }, {
        text: 'Stock',
        value: 'stock',
        sortable: false
      }, {
        text: 'Precio Venta',
        value: 'precio_venta',
        sortable: false
      }, {
        text: 'Descripción',
        value: 'descripcion',
        sortable: false
      }, {
        text: 'Estado',
        value: 'estado',
        sortable: false
      }],
      verDetalle: 0,
      valida: 0,
      validaMensaje: [],
      adModal: 0,
      adAccion: 0,
      adNombre: '',
      adId: '',
      comprobanteModal: 0,
      fecha: null
    };
  },
  computed: {
    calcularTotal: function calcularTotal() {
      var resultado = 0.0;

      for (var i = 0; i < this.detalles.length; i++) {
        resultado = resultado + (this.detalles[i].cantidad * this.detalles[i].precio - this.detalles[i].descuento);
      }

      return resultado;
    }
  },
  watch: {
    dialog: function dialog(val) {
      val || this.close();
    }
  },
  created: function created() {
    this.listar();
    this.selectPersona();
  },
  methods: {
    crearPDF: function crearPDF() {
      var quotes = document.getElementById('factura');
      html2canvas(quotes).then(function (canvas) {
        var imgData = canvas.toDataURL('image/png');
        var imgWidth = 210;
        var pageHeight = 295;
        var imgHeight = canvas.height * imgWidth / canvas.width;
        var heightLeft = imgHeight;
        var doc = new jsPDF('p', 'mm', 'a4');
        var position = 0;
        doc.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
        heightLeft -= pageHeight;

        while (heightLeft >= 0) {
          position = heightLeft - imgHeight;
          doc.addPage();
          doc.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
          heightLeft -= pageHeight;
        }

        doc.save('ComprobanteVenta.pdf');
      });
    },
    mostrarComprobante: function mostrarComprobante(item) {
      this.limpiar();
      this.tipo_comprobante = item.tipo_comprobante;
      this.serie_comprobante = item.serie_comprobante;
      this.num_comprobante = item.num_comprobante;
      this.fecha = item.createdAt;
      this.persona = item.persona;
      this.impuesto = item.impuesto;
      this.listarDetalles(item._id);
      this.comprobanteModal = 1;
    },
    ocultarComprobante: function ocultarComprobante() {
      this.comprobanteModal = 0;
    },
    selectPersona: function selectPersona() {
      var me = this;
      var personaArray = [];
      var header = {
        "Token": this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.get('persona/listClientes', configuracion).then(function (response) {
        personaArray = response.data;
        personaArray.map(function (x) {
          me.personas.push({
            text: x.nombre,
            value: x._id
          });
        });
      }).catch(function (error) {
        console.log(error);
      });
    },
    buscarCodigo: function buscarCodigo() {
      var me = this;
      me.errorArticulo = null;
      var header = {
        "Token": this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.get('articulo/queryCodigo?codigo=' + this.codigo, configuracion).then(function (response) {
        me.agregarDetalle(response.data);
      }).catch(function (error) {
        me.errorArticulo = 'No existe el artículo.';
      });
    },
    agregarDetalle: function agregarDetalle(data) {
      this.errorArticulo = null;

      if (this.encuentra(data._id) == true) {
        this.errorArticulo = 'El artículo ya ha sido agregado.';
      } else {
        this.detalles.push({
          _id: data._id,
          articulo: data.nombre,
          cantidad: 1,
          precio: data.precio_venta,
          descuento: 0
        });
        this.codigo = '';
      }
    },
    encuentra: function encuentra(id) {
      var sw = 0;

      for (var i = 0; i < this.detalles.length; i++) {
        if (this.detalles[i]._id == id) {
          sw = true;
        }
      }

      return sw;
    },
    eliminarDetalle: function eliminarDetalle(arr, item) {
      var i = arr.indexOf(item);

      if (i != -1) {
        arr.splice(i, 1);
      }
    },
    listarArticulos: function listarArticulos() {
      var me = this;
      var header = {
        "Token": this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.get('articulo/list?valor=' + this.texto, configuracion).then(function (response) {
        console.log(response.data);
        me.articulos = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    mostrarModalArticulos: function mostrarModalArticulos() {
      this.dialog = 1;
    },
    listarDetalles: function listarDetalles(id) {
      var me = this;
      var header = {
        "Token": this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.get('venta/query?_id=' + id, configuracion).then(function (response) {
        me.detalles = response.data.detalles;
      }).catch(function (error) {
        console.log(error);
      });
    },
    verIngreso: function verIngreso(item) {
      this.limpiar();
      this.tipo_comprobante = item.tipo_comprobante;
      this.serie_comprobante = item.serie_comprobante;
      this.num_comprobante = item.num_comprobante;
      this.persona = item.persona._id;
      this.impuesto = item.impuesto;
      this.listarDetalles(item._id);
      this.verNuevo = 1;
      this.verDetalle = 1;
    },
    listar: function listar() {
      var me = this;
      var header = {
        "Token": this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.get('venta/list', configuracion).then(function (response) {
        me.ventas = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    limpiar: function limpiar() {
      this._id = '';
      this.tipo_comprobante = '';
      this.serie_comprobante = '';
      this.num_comprobante = '';
      this.impuesto = 0.18;
      this.codigo = '';
      this.total = 0;
      this.totalParcial = 0;
      this.totalImpuesto = 0;
      this.detalles = [];
      this.verNuevo = 0;
      this.valida = 0;
      this.validaMensaje = [];
      this.verDetalle = 0;
    },
    validar: function validar() {
      this.valida = 0;
      this.validaMensaje = [];

      if (!this.persona) {
        this.validaMensaje.push('Seleccione un cliente.');
      }

      if (!this.tipo_comprobante) {
        this.validaMensaje.push('Seleccione un tipo de comprobante.');
      }

      if (!this.num_comprobante) {
        this.validaMensaje.push('Ingrese el número del comprobante.');
      }

      if (!this.impuesto || this.impuesto < 0 || this.impuesto > 1) {
        this.validaMensaje.push('Ingrese un impuesto válido.');
      }

      if (this.detalles.length <= 0) {
        this.validaMensaje.push('Ingrese al menos un artículo al detalle');
      }

      if (this.validaMensaje.length) {
        this.valida = 1;
      }

      return this.valida;
    },
    mostrarNuevo: function mostrarNuevo() {
      this.verNuevo = 1;
    },
    ocultarNuevo: function ocultarNuevo() {
      this.verNuevo = 0;
      this.limpiar();
    },
    guardar: function guardar() {
      var me = this;
      var header = {
        "Token": this.$store.state.token
      };
      var configuracion = {
        headers: header
      };

      if (this.validar()) {
        return;
      } //Código para guardar


      axios.post('venta/add', {
        'persona': this.persona,
        'usuario': this.$store.state.usuario._id,
        'tipo_comprobante': this.tipo_comprobante,
        'serie_comprobante': this.serie_comprobante,
        'num_comprobante': this.num_comprobante,
        'impuesto': this.impuesto,
        'total': this.total,
        'detalles': this.detalles
      }, configuracion).then(function (response) {
        me.limpiar();
        me.close();
        me.listar();
      }).catch(function (error) {
        console.log(error);
      });
    },
    activarDesactivarMostrar: function activarDesactivarMostrar(accion, item) {
      this.adModal = 1;
      this.adNombre = item.num_comprobante;
      this.adId = item._id;

      if (accion == 1) {
        this.adAccion = 1;
      } else if (accion == 2) {
        this.adAccion = 2;
      } else {
        this.adModal = 0;
      }
    },
    activarDesactivarCerrar: function activarDesactivarCerrar() {
      this.adModal = 0;
    },
    activar: function activar() {
      var me = this;
      var header = {
        "Token": this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.put('venta/activate', {
        '_id': this.adId
      }, configuracion).then(function (response) {
        me.adModal = 0;
        me.adAccion = 0;
        me.adNombre = '';
        me.adId = '';
        me.listar();
      }).catch(function (error) {
        console.log(error);
      });
    },
    desactivar: function desactivar() {
      var me = this;
      var header = {
        "Token": this.$store.state.token
      };
      var configuracion = {
        headers: header
      };
      axios.put('venta/deactivate', {
        '_id': this.adId
      }, configuracion).then(function (response) {
        me.adModal = 0;
        me.adAccion = 0;
        me.adNombre = '';
        me.adId = '';
        me.listar();
      }).catch(function (error) {
        console.log(error);
      });
    },
    close: function close() {
      this.dialog = false;
    }
  }
};
